import React, {useEffect, useState} from 'react';
import './App.css';
import Message from './interfaces';

function App() {

  const [backendData, setBackendData] = useState<Message>({} as Message);

  // fetch "/api" location
  useEffect(() => {
    fetch('/api')
    .then(
      response => response.json() // turn the response in json
    )
    .then(
      data => {
        setBackendData(data) // set the data into the data we get
      }
    )
  }, []) // empty array so this runs on first render of the component

  return (
    <div>
      {(typeof backendData === 'undefined') ? (
        <p>Loading...</p>
      ) : (
        backendData.message
      )}
    </div>
  );
}

export default App;
